mapper.select:
SELECT upstreamAppkey,upstreamAppid,backUrl
FROM a_article
where appId = #{appId} and articleId = #{slotId}

SELECT appId,articleId as slotId,backUrl
FROM a_article
where upstreamAppkey = #{upstreamAppkey} and upstreamAppid = #{upstreamAppid}

select eventStatus
from a_slot
where slotId = #{slotId}
